import React from 'react';
//import RunForm from './RunForm';

export default function RunInfo(props) {

	const timeInSeconds = (props.runInfo.hour*3600) + (props.runInfo.minute*60) + (1*props.runInfo.second);
	var avgMile = (props.runInfo.mileage!==0 && timeInSeconds!==0) ? timeInSeconds/props.runInfo.mileage : 0;
	const hourAvg = Math.floor(avgMile/3600);
	avgMile %= 3600;
	const minuteAvg = Math.floor(avgMile/60);
	const secondAvg = Math.floor(avgMile%60);

	const splits = [];


	return (
		<div className="runInfo">
			{props.runInfo.mileage==="0" ? (
				<h2>
					Fill out the form!
				</h2>
			) : <h2>{props.runInfo.mileage} miles{/*props.runInfo.unit*/} run</h2>}

			<p>Total Time: {timeInSeconds}</p>
			<p>Average Time: {avgMile}</p>
			<h3>Pace Per Mile:</h3>
			<p>{hourAvg>0 ? hourAvg+":" : ""}{minuteAvg < 10 ? "0"+minuteAvg : minuteAvg}:{secondAvg < 10 ? "0"+secondAvg : secondAvg}</p>


						{/*this.splits.map((runLog,index)=> (
				<RunLog key={index} log={runLog} />
			))*/}
		</div>
	);
}
