import React, { Component } from 'react';
import { Form } from 'semantic-ui-react'
import RunInfo from './RunInfo';

const units = [
	{key:"mi", text:"Miles", value:"mi"},
	{key:"km", text:"Kilometers", value:"mi"}
]

export default class RunForm extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loading: true,
			error: "",
			mileage: 0,
			unit: "none",
			hour: 0,
			minute: 0,
			second: 0
		};

		this.handleFieldChange = this.handleFieldChange.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
	}

	handleFieldChange = event => {
		const { value, name } = event.target;
		console.log(event.target);
		this.setState({
			...this.state,
			[name]: value
		});
		if (event.target.name==="hour" || event.target.name==="minute" || event.target.name==="second"){
			this.setState({ 
				loading: false 
			});
		}

		RunInfo(this.state);
	};

	onSubmit(e) {
		e.preventDefault();

		if (!this.isFormValid()) {
			this.setState({ error: "All fields are required." });
			return;
		}


	}

	isFormValid() {
		return this.state.mileage !== "0" && this.state.unit !== "none";
	}



	renderError() {
		return this.state.error ? (
			<div className="alert-red">{this.state.error}</div>
		) : null;
	}

	render() {
		return (
			<React.Fragment>
				<Form className="ui centered">
					<Form.Group>
						<Form.Input
							label="Amount Run"
							type="number"
							name="mileage"
							value={this.state.mileage}
							size="huge"
							width={4}
							min="0"
							/>
						<Form.Select
							fluid
							label="Unit"
							name="unit"
							options={units}

							placeholder="Select One"
							size="huge"
							width={5}
						 />
					</Form.Group>
					<Form.Group widths="equal">
						<Form.Input 
							fluid
							label="Hours"
							name="hour"
							value={this.state.hour}
							type="number"
							min="0"
							size="huge"
						/>
						<Form.Input
							fluid
							label="Minutes"
							name="minute"
							value={this.state.minute}
							type="number"
							min="0"
							size="huge"
						/>
						<Form.Input
							fluid
							label="Seconds"
							name="second"
							value={this.state.second}
							type="number"
							min="0"
							size="huge"
						/>
					</Form.Group>
					<Form.Button>Enter Run</Form.Button>
				</Form>
			</React.Fragment>
		);
	}
}
