import React, { Component } from 'react';

export default class RunForm extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loading: false,
			error: "",

			runLog: {
				name: "",
				mileage: 0,
				runTime: ""
			}
		};

		this.handleFieldChange = this.handleFieldChange.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
	}

	handleFieldChange = event => {
		const { value, name, runTime } = event.target;

		this.setState({
			...this.state,
			runLog: {
				...this.state.runLog,
				[name]: value,
				[runTime]: value
			}
		});
	};

	onSubmit(e) {
		e.preventDefault();

		if (!this.isFormValid()) {
			this.setState({ error: "All fields are required." });
			return;
		}
	}

	isFormValid() {
		return this.state.runLog.name !== "" && this.state.runLog.message !== "";
	}



	renderError() {
		return this.state.error ? (
			<div className="alert-red">{this.state.error}</div>
		) : null;
	}

	render() {
		return (
			<React.Fragment>
				<form method="post" onSubmit={this.onSubmit}>
					<div className="form-group">
						<div>
							<input
							onChange={this.handleFieldChange}
							value={this.state.runLog.name}
							className="form-control"
							placeholder="Your Name"
							name="name"
							type="text"
						/>
						</div>
						<div>
							<input
							onChange={this.handleFieldChange}
							value={this.state.runLog.mileage}
							className="form-control"
							min="0"
							name="mileage"
							type="number"
						/>
						</div>
					</div>

					{this.renderError()}

					<div className="form-group">
						<button disabled={this.state.loading} className="btn btn-primary">
							Submit Run âž¤
						</button>
					</div>
				</form>
			</React.Fragment>
		);
	}
}
