import React from 'react';
//import RunForm from './RunForm';

export default function RunInfo(props) {

	const timeInSeconds = (props.runInfo.hour*3600) + (props.runInfo.minute*60) + (1*props.runInfo.second);
	const avgMile = (props.runInfo.mileage!==0 && timeInSeconds!==0) ? timeInSeconds/props.runInfo.mileage : 0;
	hourAvg = avgMile/3600;
	minuteAvg = hourAvg/60;
	secondAvg = minuteAvg/60;

	return (
		<div className="runInfo">
			{props.runInfo.mileage==="0" ? (
				<h2>
					Fill out the form!
				</h2>
			) : <h2>{props.runInfo.mileage} miles{/*props.runInfo.unit*/} run</h2>}

			<p>Total Time: {timeInSeconds}</p>
			<p>Average Time: {avgMile}{}:{}</p>


						{/*props.runLogs.map((runLog,index)=> (
				<RunLog key={index} log={runLog} />
			))*/}
		</div>
	);
}
