import React, { Component } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react'


export default class RunForm extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loading: true,
			error: "",
			mileage: 0,
			unit: "none",
			hour: 0,
			minute: 0,
			second: 0
		};

		this.handleFieldChange = this.handleFieldChange.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
	}

	handleFieldChange = event => {
		const { value, name } = event.target;
		this.setState({
			...this.state,
			[name]: value
		});
		if (event.target.name==="hour" || event.target.name==="minute" || event.target.name==="second"){
			this.setState({ 
				loading: false 
			});
		}
	};

	onSubmit(e) {
		e.preventDefault();

		if (!this.isFormValid()) {
			this.setState({ error: "All fields are required." });
			return;
		}


	}

	isFormValid() {
		return this.state.mileage !== "0" && this.state.unit !== "none";
	}



	renderError() {
		return this.state.error ? (
			<div className="alert-red">{this.state.error}</div>
		) : null;
	}

	render() {
		return (
			<React.Fragment>
				<Form>

				</Form>
			</React.Fragment>
		);
	}
}
