import React, { Component } from 'react';

export default class RunForm extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loading: false,
			error: "",

			runLog: {
				name: "",
				mileage: 0,
				runTime: ""
			}
		};

		this.handleFieldChange = this.handleFieldChange.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
	}

	handleFieldChange = event => {
		const { value, name } = event.target;

		this.setState({
			...this.state,
			runLog: {
				...this.state.runLog,
				[name]: value
			}
		});
	};

	onSubmit(e) {
		e.preventDefault();

		if (!this.isFormValid()) {
			this.setState({ error: "All fields are required." });
			return;
		}

		this.setState({ error: "", loading: true });
		let runLog = this.state;
		fetch("http://localhost:7777", {
			method: "post",
			body: JSON.stringify(runLog)
		})
			.then(res => res.json())
			.then(res => {
				if (res.error) {
					this.setState({ loading: false, error: res.error });
				} else {
					// add time return from api and push comment to parent state
					runLog.time = res.time;
					this.props.addComment(comment);

					// clear the message box
					this.setState({
						loading: false,
						mileage: { ...mileage, mileage: 0 },
						runTime: { ...runTime, runTime: ""}
					});
				}
			})
			.catch(err => {
				this.setState({
					error: "Something went wrong while submitting form.",
					loading: false
				});
			});
		}

	isFormValid() {
		return this.state.runLog.name !== "" && this.state.runLog.message !== "";
	}



	renderError() {
		return this.state.error ? (
			<div className="alert-red">{this.state.error}</div>
		) : null;
	}

	render() {
		<React.Fragment>
			<form method="post" onSubmit={this.onSubmit}>
				<div className="form-group">
					<input
						onChange={this.handleFieldChange}
						value={this.state.runLog.name}
						className="form-control"
						placeholder="Your Name"
						name="name"
						type="text"
					/>
				</div>

				<div className="form-group">
					<textarea
						onChange={this.handleFieldChange}
						value={this.state.runLog.mileage}
						className="form-control"
						name="mileage"
						type="number"
					/>
				</div>
				<div className="form-group">
					<input
						onChange={this.handleFieldChange}
						value={this.state.runLog.name}
						className="form-control"
						placeholder="00:00"
						name="time"
						type="text"
					/>
				</div>

				{this.renderError()}

				<div className="form-group">
					<button disabled={this.state.loading} className="btn btn-primary">
						Log Run ➤
					</button>
				</div>
			</form>
		</React.Fragment>
	}
}
