import React, { Component } from 'react';
import { Form } from 'semantic-ui-react'
//import RunInfo from './RunInfo';

const units = [
	{key:"mi", name:"unit", text:"Miles", value:"mi"},
	{key:"km", name:"unit", text:"Kilometers", value:"km"}
]

export default class RunForm extends Component {
	constructor(props) {
		super(props);
		this.state = {
			...props.runData,
			error: ""
		};

		this.handleFieldChange = this.handleFieldChange.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
	}

	handleChange = (e, { value }) => this.setState({ value })

	handleFieldChange = event => {
		const { value, name } = event.target;
		this.setState({
			...this.state,
			[name]: value,
			submitted: false
		});

		//this.props.handler(this.state);

		if ([name]==="hour" || [name]==="minute" || [name]==="second"){
			this.setState({
				...this.state,
				spinning: false 
			});
		}
	};

	onSubmit(e) {
		e.preventDefault();
		this.setState({ submitted: true });
		this.props.handler(this.state);
		if (!this.isFormValid()) {
			this.setState({ error: "All fields are required." });
			return;
		}

	}

	isFormValid() {
		return this.state.mileage !== "0" && this.state.unit !== "";
	}



	renderError() {
		return this.state.error ? (
			<div className="alert-red">{this.state.error}</div>
		) : null;
	}

	render() {
		return (
			<React.Fragment>
				<Form className="ui centered">
					<Form.Group>
						<Form.Input
							label="Amount Run"
							type="number"
							onChange={ this.handleFieldChange }
							name="mileage"
							value={this.state.mileage}
							size="huge"
							width={4}
							min="0"
							/>
						<Form.Select
							fluid
							label="Unit"
							onChange={ this.handleFieldChange }
							name="unit"
							options={units}
							value={this.state.unit.value}
							placeholder="Select One"
							size="huge"
							width={5}
						 />
					</Form.Group>
					<Form.Group widths="equal">
						<Form.Input 
							fluid
							label="Hours"
							name="hour"
							value={this.state.hour}
							onChange={this.handleFieldChange}
							type="number"
							min="0"
							size="huge"
						/>
						<Form.Input
							fluid
							label="Minutes"
							name="minute"
							value={this.state.minute}
							onChange={this.handleFieldChange}
							type="number"
							min="0"
							size="huge"
						/>
						<Form.Input
							fluid
							label="Seconds"
							name="second"
							value={this.state.second}
							onChange={this.handleFieldChange}
							type="number"
							min="0"
							size="huge"
						/>
					</Form.Group>
					<Form.Button onClick={this.onSubmit}>Update Run</Form.Button>
				</Form>
			</React.Fragment>
		);
	}
}
