import React, { Component } from 'react';

export default class RunForm extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loading: true,
			error: "",
			mileage: 0,
			unit: "none",
			hour: 0,
			minute: 0,
			second: 0
		};

		this.handleFieldChange = this.handleFieldChange.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
	}

	handleFieldChange = event => {
		const { value, name } = event.target;
		this.setState({
			...this.state,
			[name]: value
			/*unit: value,
			hour: value,
			minute: value,
			second: value,
			loading: false*/
		});
		if (this.state.hour!=="0" || this.state.minute!=="0" || this.state.second !=="0"){
			this.setState({ loading:true });
		}
	};

	onSubmit(e) {
		e.preventDefault();

		if (!this.isFormValid()) {
			this.setState({ error: "All fields are required." });
			return;
		}
	}

	isFormValid() {
		return this.state.mileage !== "0" && this.state.unit !== "none";
	}



	renderError() {
		return this.state.error ? (
			<div className="alert-red">{this.state.error}</div>
		) : null;
	}

	render() {
		return (
			<React.Fragment>
				<form  onSubmit={this.onSubmit}>
					<div className="form-group">
							<input
							onChange={this.handleFieldChange}
							value={this.state.mileage}
							className="form-control"
							placeholder="Amount Run"
							name="mileage"
							type="number"
							pattern="[0-9]+"
						/>
						<select name="unit" value={this.state.unit} onChange={this.handleFieldChange}>
							<option value="none" disabled>Select One</option>
							<option value="mi">mi</option>
							<option value="km">km</option>
						</select>
					</div>
					<div>
						<input
							onChange={this.handleFieldChange}
							value={this.state.hour}
							className="form-control form-time"
							min="0"
							name="hour"
							type="number"
						/>:
						<input
							onChange={this.handleFieldChange}
							value={this.state.minute}
							className="form-control form-time"
							min="0"
							name="minute"
							type="number"
						/>:
						<input
							onChange={this.handleFieldChange}
							value={this.state.second}
							className="form-control form-time"
							min="0"
							name="second"
							type="number"
						/>
					</div>

					{this.renderError()}

					<div className="form-group">
						<button disabled={this.state.loading} className="btn btn-primary">
							Submit Run âž¤
						</button>
					</div>
				</form>
			</React.Fragment>
		);
	}
}
