import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import RunForm from './RunForm';
import RunInfo from './RunInfo';
import '../node_modules/semantic-ui-css/semantic.min.css'

class App extends Component {
  constructor(props){
    super(props);

    this.state = {
      spinning: true,
      mileage: "0",
      unit: "",
      hour: "0",
      minute: "0",
      second: "0"
    };

    this.updateInfo = this.updateInfo.bind(this);
  }

  updateInfo(props) {
    this.setState({
      ...this.state,
      mileage: props.mileage,
      unit: props.unit,
      hour: props.hour,
      minute: props.minute,
      second: props.second
    });
  }


  render() {
    const loadingSpin = this.state.spinning ? "App-logo Spin" : "App-logo";
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className={loadingSpin} alt="logo" />
          <h1 className="App-title">
            Run Analyzer
          </h1>
          <p>This webapp allows you to understand what shape you are in based on your last run pace.</p>
        </header>

        <div className="Row">
          <div className="App-column-form centered">
            <h2>Run Information</h2>
            <RunForm />
            {/* Form Component */}
          </div>
          <div className="App-column-feed">
            <RunInfo
              runInfo={this.state}
            />
            {/* Run feed Component */}
          </div>
        </div>


      </div>
    )
  }
}
