import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import RunList from './RunList';
import RunLog from './RunLog';
import RunForm from './RunForm';

class App extends Component {
  constructor(props){
    super(props);

    this.state = {
      runLogs: [],
      loading: false
    };

    this.addRunLog = this.addRunLog.bind(this);
  }

  componentDidMount() {
    // loading
    this.setState({ loading: true });

    // get all the comments
    fetch("http://localhost:7777")
      .then(res => res.json())
      .then(res => {
        this.setState({
          comments: res,
          loading: false
        });
      })
      .catch(err => {
        this.setState({ loading: false });
      });
  }

  addRunLog(runLog){
    this.setState({
      loading: false,
      runLogs: [runLog, ...this.state.runLogs]
    });
  }


  render() {
    const loadingSpin = this.state.loading ? "App-logo Spin" : "App-logo";
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className={loadingSpin} alt="logo" />
          <h1 className="App-title">
            Running Log
          </h1>
        </header>

        <div className="Row">
          <div className="App-column-form">
            <h2>Log your run!</h2>
            <RunForm addRunLog={this.addRunLog} />
            {/* Form Component */}
          </div>
          <div className="App-column-feed">
            <RunList

            />
            {/* Run feed Component */}
          </div>
        </div>


      </div>
    )
  }
}
