import React, { Component } from 'react';

export default class RunForm extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loading: false,
			error: "",

			runLog: {
				mileage: 0,
				unit: "none",
				hour: 0,
				minute: 0,
				second: 0
			}
		};

		this.handleFieldChange = this.handleFieldChange.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
	}

	handleFieldChange = event => {
		const { value, name, runTime } = event.target;

		this.setState({
			...this.state,
			runLog: {
				...this.state.runLog,
				[mileage]: value,
				[hour]: value,
				[minute]: value,
				[second]: value
			}
		});
	};

	onSubmit(e) {
		e.preventDefault();

		if (!this.isFormValid()) {
			this.setState({ error: "All fields are required." });
			return;
		}
	}

	isFormValid() {
		return this.state.runLog.mileage !== "" && this.state.runLog.unit === "none";
	}



	renderError() {
		return this.state.error ? (
			<div className="alert-red">{this.state.error}</div>
		) : null;
	}

	render() {
		return (
			<React.Fragment>
				<form method="post" onSubmit={this.onSubmit}>
					<div className="form-group">
							<input
							onChange={this.handleFieldChange}
							value={this.state.runLog.name}
							className="form-control"
							placeholder="Amount Run"
							name="mileage"
							type="number"
						/>
						<select name="unit" value={this.state.unit}>
							<option value="none" disabled>Select One</option>
							<option value="mi">mi</option>
							<option value="mi">km</option>
						</select>
					</div>
					<div>
						<input
							onChange={this.handleFieldChange}
							value={this.state.runLog.hour}
							className="form-control form-time"
							min="0"
							name="hour"
							type="number"
						/>:
						<input
							onChange={this.handleFieldChange}
							value={this.state.runLog.minute}
							className="form-control form-time"
							min="0"
							name="minute"
							type="number"
						/>:
						<input
							onChange={this.handleFieldChange}
							value={this.state.runLog.second}
							className="form-control form-time"
							min="0"
							name="second"
							type="number"
						/>
					</div>

					{this.renderError()}

					<div className="form-group">
						<button disabled={this.state.loading} className="btn btn-primary">
							Submit Run âž¤
						</button>
					</div>
				</form>
			</React.Fragment>
		);
	}
}
